import React, { useEffect,useState } from "react";
import { Link } from "react-router-dom";
import BannerImage from "../assets/bg.avif";
import "../styles/Home.css";
import { useLocation } from "react-router-dom";
import EmpNavbar from "../components/Empnavbar";


function Employer(){
     const [data,setdata]=useState({"cname":"XXX"});
     const location=useLocation();
     const d=location.state;
    function onLoad(){
    
  fetch("http://127.0.0.1:8080/getempldata/"+d.user,
  {
      method: "GET",
      headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
      },
     
  }
).then((res) => res.json()).then((data) => { setdata(data) });
}
    if(data["cname"]==="XXX"){
      onLoad();
    }
        
    
    
    return(
      <div>
        <EmpNavbar />
        <div className="home" style={{ backgroundImage: `url(${BannerImage})` }}>
        <div className="headerContainer">
          <h2 style={{color:"white"}}>Welcome, {data["cname"]}</h2>
          <h1> Job Search Portal</h1>
          <p> Opportunities don't happen</p>
          <Link to="/AddJobs">
            <button> ADD JOB </button>
          </Link>
        </div>
      </div>
      </div>
    );
}

export default Employer;
