import React, { useEffect,useState } from "react";
import { Link } from "react-router-dom";
import BannerImage from "../assets/bg.avif";
import "../styles/Home.css";
import { useLocation } from "react-router-dom";
import EmpNavbar from "../components/Empnavbar";

function Employer(){
    const location=useLocation();
    const d=location.state;
    const [data,setdata]=useState({"cname":3333});
    fetch("http://127.0.0.1:8080/getempldata/"+d.user,
        {
            method: "GET",
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
           
        }
    ).then((res) => res.json()).then((data) => { setdata(data) });
    console.log(data);
    return(
      <div>
        <EmpNavbar />
        <div className="home" style={{ backgroundImage: `url(${BannerImage})` }}>
        <div className="headerContainer">
          <h1>Welcome, {data["PromiseResult"]["cname"]}</h1>
          <h1> Job Search Portal</h1>
          <p> Opportunities don't happen</p>
          <Link to="/AddJobs">
            <button> ADD JOB </button>
          </Link>
        </div>
      </div>
      </div>
    );
}

export default Employer;
