import React from "react";
import { useState } from "react";
import '../styles/Jobs.css'
import { useLocation,useHistory } from "react-router-dom/cjs/react-router-dom.min";
function JobPost(employerid,jobid,location,description,salary,experience){
    return (
    <div className="jobsItem">
      <div> </div>
      <h4> Employer ID :{employerid} </h4>
      <h4> Job ID: {jobid}</h4>
      <h4> Location: {location}</h4>
      <h4>Description: {description}</h4>
      <h4>Salary: {salary}</h4>
      <h4>Experience: {experience}</h4>


      

    </div>
      );
}

function EmpJobs(){
    const[data,setdata]=useState("");
    let d=useLocation();

    if(data==[]){
        fetch("http://127.0.0.1:8080/fetchempjobs/"+d.id,
        {
            method: "GET",
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
           
        }
      ).then((res) => res.json()).then((data) => { setdata(data) });

      console.log(data);
        
    }

    return(
        <div>
            {data=="" && <div>No Job Listings Found</div>}
            {
                data && <div>

                    {data.map((i,j) =>
                    {
                        return(
                            <JobPost
                               employerid={i.empid}
                               jobid={i.jobid}
                               location={i.location}
                               description={i.jobdesc}
                               salary={i.sal}
                               experience={i.exp}
                            /> 
                        );
                    })}
                     </div>
            }
        </div>
    );


}

export default EmpJobs;
