import React, { useEffect,useState } from "react";
import '../styles/Login.css';
import BannerImage from '../assets/bg.avif';

function EmployerUpdate(){
    const[compname,setcompname]=useState("");
    const[descp,setdescp]=useState("");
    const[loc,setloc]=useState("");
    const[cpass,setcpass]=useState("");
    const[compmail,setcompmail]=useState("");
    return(
        <div className="container" style={{ backgroundImage: `url(${BannerImage})` }}>
        
        <div className="login-container"> {/*Employer Data Updation*/ }
        <h2>Update Employer Details</h2>
        <form onSubmit={handleERegistration}>
          <div className="form-group">
            <label>Company Name:</label>
            <input
              type="text"
              
              onChange={(e) => setcompname(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Company Email:</label>
            <input
              type="email"
              
              onChange={(e) => setcompmail(e.target.value)}
              required
            />
          <div></div>
          <div>&nbsp;</div>
            
            
            
          </div>
          
          <div className="form-group">
            <label>Description about the company :</label>
            <textarea
              
              rows="10"
              cols="42"
              onChange={(e) => setdescp(e.target.value)}
              required
            />
          </div>
  
          <div className="form-group">
            <label>Locations:</label>
            <Select
      
            isMulti
            name="colors"
            options={locoptions}
            onChange={setloc}
            className="basic-multi-select"
            classNamePrefix="select"
            />
            
          </div>
          
          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
             
              onChange={(e) => setcpass(e.target.value)}
              required
            />
          </div>
          
          
          <button type="submit" onClick={handleERegistration}>Register</button>
          
    </form>
      </div>
      
      </div>
       
      
    );
}

export default EmployerUpdate;
