import React, { useState } from 'react';
import '../styles/Login.css';
import BannerImage from '../assets/bg.avif'
import {Link} from 'react-router-dom';
import Select from 'react-select';

const Registration = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
 
  const [degree,setdegree]=useState('');
  const [branch,setbranch]=useState('');
  const [insti,setInsti]=useState('');
  const [skill,setskill]=useState([]);
  const [role,setrole]=useState(null);
  const skoptions = [
    { label: "Java", value: "Java" },
    { label: "Springboot", value: "Springboot" },
    { label: "Data Analytics", value: "Data Analytics"},
    { label: "Computer Networks", value: "Networks"},
    { label: "Auto CADD", value: "Auto CADD"},
    { label: "Leadership", value: "Leadership"},
   
    
    
  ];

  const degoptions=[
    {label:"BE",value:"BE"},
    {label:"BTech",value:"BTech"},
    {label:"BS",value:"BS"},
    {label:"BsC",value:"BsC"},
    {label:"Diploma",value:"Diploma"},
  ];
  const roleoptions=[
    {label:"Student",value:"Student"},
    {label:"Employer",value:"Employer"},

  ]
  const broptions=[
    {label:"CSE",value:"CSE"},
    {label:"ECE",value:"ECE"},
    {label:"EEE",value:"EEE"},
    {label:"Data Scienece",value:"Data Science"},
    {label:"Mechanical",value:"Mechanical"},
    {label:"Civil",value:"Civil"},
  ];
  

  const handleDegree = (event) => {
    setdegree(event.target.value);
  };

  const handleRegistration = (e) => {
    e.preventDefault();
    // Implement registration logic (e.g., send a POST request to your backend)

    // For this example, we'll simply log the data
    console.log('Name:', name);
    console.log('Email:', email);
    console.log('Password:', password);
    console.log('Degree ', degree);
    console.log('Branch ', branch);
    console.log('Insti ', insti);
    console.log('skill ', skill);

  };
  return (
    <div className="container" style={{ backgroundImage: `url(${BannerImage})` }}>
      <div className='login-container'>
          <label>Role:</label>
          <Select
        className="basic-single"
        classNamePrefix="select"
        onChange={setrole}
        
        name="Degree"
        options={roleoptions}
      />
          </div>
    {role.value==="student" && <div className="login-container">
      <h2>Register as Student</h2>
      <form onSubmit={handleRegistration}>
        <div className="form-group">
          <label>Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        <div></div>
        <div>&nbsp;</div>
          
          <div className='form-group'>
          <label>Degree:</label>
          <Select
        className="basic-single"
        classNamePrefix="select"
        onChange={setdegree}
        
        name="Degree"
        options={degoptions}
      />
          </div>
          <div className='form-group'>
          <label>Branch:</label>
          <Select
        className="basic-single"
        classNamePrefix="select"
        onChange={setbranch}
        
        name="Branch"
        options={broptions}
      />
          </div>
        </div>

        <div className="form-group">
          <label>Institution:</label>
          <input
            type="text"
            
            onChange={(e) => setInsti(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Skill Set:</label>
          <Select
    
    isMulti
    name="colors"
    options={skoptions}
    onChange={setskill}
    className="basic-multi-select"
    classNamePrefix="select"
  />
          
        </div>
        
        <div className="form-group">
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {/*<div className="form-group">
          <label>:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>*/}
        
        <button type="submit" onClick={handleRegistration}>Register</button>
        
      </form>
    </div>}
    </div>
  );
  
};

export default Registration;
