import React, { useState } from 'react';
import '../styles/Login.css';
import BannerImage from '../assets/bg.avif'
import {Link} from 'react-router-dom'
import Select from 'react-select';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role,setrole]=useState({label:"Student",value:"student"});

  const roleoptions=[
    {label:"Student",value:"student"},
    {label:"Employer",value:"employer"},

  ]
  const [data, setData] = useState([{}]);

  fetch("http://127.0.0.1/login",
            {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                body: JSON.stringify({
                    uname: email,
                    pass: password,
                    role:role

                })
            }
        ).then((res) => res.json()).then((data) => { setData(data) });
  const handleLogin = (e) => {
    e.preventDefault();

    let em=email;
    let pa=password;
    let ro=role;

    if (data['RES'] === "OK" && role==="student") {
      history.push({
          pathname: '/student',
          state: {'user':email}
        });
      }
    else if(data['RES'] === "OK" && role==="student"){
      history.push({
        pathname: '/employer',
        state: {'user':email}
      });
    }
  }

    
    
  

  return (
    <div className="container" style={{ backgroundImage: `url(${BannerImage})` }}>
    <div className="login-container" >
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className='form-group'>
          <label>Role:</label>
          <Select
        className="basic-single"
        classNamePrefix="select"
        onChange={setrole}
        
        name="Degree"
        options={roleoptions}
         />
         </div>
        <div className="form-group">
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        
        <button type="submit" onClick={handleLogin}>Login</button>
      
      </form>
    </div>
    </div>
  );
};

export default Login;
