import React from "react";
import { useState } from "react";
import '../styles/Jobs.css'
import { useLocation,useHistory } from "react-router-dom/cjs/react-router-dom.min";
function JobPost(props){
    return (
    <div className="jobsItem">
      <div> </div>
      <h4> Employer ID :{props.employerid} </h4>
      <h4> Job ID: {props.jobid}</h4>
      <h4> Location: {props.location}</h4>
      <h4>Description: {props.description}</h4>
      <h4>Salary: {props.salary}</h4>
      <h4>Experience: {props.experience}</h4>


      

    </div>
      );
}

function EmpJobs(){
    const[data,setdata]=useState(null);
    let d=useLocation();

    if(data==null){
        fetch("http://127.0.0.1:8080/fetchempjobs/"+"4",
        {
            method: "GET",
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
           
        }
      ).then((res) => res.json()).then((data) => { setdata(data) });

      //console.log(data);
        
    }

    return(
        <div> <h2 style={{textAlign:"center"}}>Jobs Posted</h2>
           
            {
                data && <div>

                    {data.map((i,j) =>
                    {   //console.log(data);
                        return(
                            <JobPost
                               employerid={i.empid}
                               jobid={i.jobid}
                               location={i.location}
                               description={i.jobdesc}
                               salary={i.sal}
                               experience={i.exp}
                            /> 
                        );
                    })}
                     </div>
            }
        </div>
    );


}

export default EmpJobs;
